const showDropdown=(o,e)=>{const s=document.querySelector(o),t=document.querySelector(e);s.addEventListener("click",o=>{t.classList.toggle("show-dropdown"),o.stopPropagation()}),document.addEventListener("click",o=>{t.contains(o.target)||s.contains(o.target)||t.classList.remove("show-dropdown")})};showDropdown(".open-user-manage",".user-session");